import 'package:flutter/material.dart';
import 'package:meu_projeto_integrador/model/item_model.dart';
import 'package:meu_projeto_integrador/widgets/item_list_card.dart';

class ItemListScreen extends StatelessWidget {
  const ItemListScreen({super.key});

  static final List<Item> _items = [
    Item(
      id: 1,
      name: 'Smartphone XYZ',
      price: 2000.00,
      imageUrl:
          'data:image/jpeg;base64,',
          description: 'veja series e filmes com alta qualidade',
    ),
    Item(
      id: 2,
      name: 'Marea Turbo',
      price: 20000.00,
      imageUrl:
          'data:image/jpeg;base64,',
      description: 'Exploxao de emoes',
    ),
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Item List'),
      ),
      body: ListView.builder(
        itemCount: _items.length,
        itemBuilder: (context, index) {
          final item = _items[index];
          return ItemListCard(
            item: item,
            onTap: () {
              // Handle item tap
              ScaffoldMessenger.of(context).showSnackBar(
                SnackBar(content: Text('Clicou no item ${item.name}')),
              );
            },
          );
        },
      ),
    );
  }
}